%% Parameters
takeOffStartTime = 3;
totalTime = takeOffStartTime + 3;
spineStartAngle = -5;    %deg
takeOffPeriod = 0.1;    %sec

numTrajPts = 10 + 1;
dt = takeOffPeriod/(numTrajPts-1);
waitTimePts = takeOffStartTime/dt;
takeOffPts = numTrajPts;
totalNumPts = (totalTime)/dt + 1;
ballisticTimePts = totalNumPts - waitTimePts - takeOffPts;

%% Commands
%Leg Position command vectors: Time, Knee Angle, Hip Angle, Foot Angle
% LegPosition = [linspace(0,totalTime,totalNumPts)' [150*ones(waitTimePts,1); linspace(150,40,takeOffTimeSteps)'; 40*ones(waitTimePts,1)], [-75*ones(waitTimeSteps,1); linspace(-75,-20,takeOffTimeSteps)'], [-75*ones(waitTimeSteps,1); linspace(-75,-20,takeOffTimeSteps)']];
LegPosition = [linspace(0,totalTime,totalNumPts)' ...
    [150*ones(waitTimePts,1); linspace(150,40,takeOffPts)'; 40*ones(ballisticTimePts,1)],...
    [-75*ones(waitTimePts,1); linspace(-75,-20,takeOffPts)'; -20*ones(ballisticTimePts,1)],...
    [-75*ones(waitTimePts,1); linspace(-60,-5,takeOffPts)'; -5*ones(ballisticTimePts,1)]];

 SpinePosition = [linspace(0,totalTime,totalNumPts)' 0*ones(totalNumPts,1)];
 ShoulderPosition = [linspace(0,totalTime,totalNumPts)' ones(totalNumPts,1)*20];
